import React from 'react';
import { Scroll } from 'react-scroll';


class Intro extends React.Component {


  scrollPortfolio = () => {
    var Scroll  = require('react-scroll');
    var Element = Scroll.Element;
    var scroller = Scroll.scroller;
    scroller.scrollTo("portfolio-comp", {
      duration: 1500,
      delay: 100,
      smooth: true,
      containerId: 'p-comp',
      offset: 50,
    })


  }


  render() {

  return (
    <div className="intro-comp" id="i-comp">
      <div className="intro-body">
      <h2 className="grey-text"> Hi! I'm <span className="highlight"> Herman</span>, a <span className="highlight" >software engineer </span>
        <br/>living and working in <span className="highlight">NYC</span>
      </h2>
      <div id="btn">
        <a onClick={this.scrollPortfolio} className="btn-large waves-effect waves-light" href="my portfolio button">View My Work</a>
      </div>
      </div>
    </div>
  )
}
}

export default Intro;
